(function($){

	$(document).ready(function(){

		// fixed menu
		var nav = jQuery('.main-navigation');
		jQuery(window).on('scroll', function(){
			var fixednav = jQuery(this).scrollTop();

			if(fixednav>50){
				nav.addClass('customNav');
			} else {
				nav.removeClass('customNav');
			}
		});
	});

})(jQuery);